import _iadd from './_iadd';
import _isub from './_isub';
import _montgomery from './_montgomery';
import _mul from './_mul';
import _redc from './_redc';
import modN from './modN';
import modR from './modR';

export default {
	Montgomery,
	_iadd,
	_isub,
	_montgomery,
	_mul,
	_redc,
	modN,
	modR
};

export {Montgomery, _iadd, _isub, _montgomery, _mul, _redc, modN, modR};

import {
	_alloc,
	_zeros,
	_copy,
	_idivmod,
	_trim_positive,
	_cmp_n
} from '@aureooms/js-integer-big-endian';

import modR from './modR';

export default function (r, N, x) {
	const k = N.length;
	const xj = x.length;
	const xi = _trim_positive(x, 0, xj);
	const xn = xj - xi;
	if (xn > k || (xn === k && _cmp_n(x, xi, xj, N, 0) >= 0)) {
		const xmodN = _alloc(xn); // TODO Use UintXArray ?
		_copy(x, xi, xj, xmodN, 0);
		const _ = _zeros(xn); // TODO use _imod once implemented
		_idivmod(r, xmodN, 0, xn, N, 0, k, _, 0, xn);
		return modR(k, xmodN);
	}

	const xmodN = _zeros(k); // TODO Use UintXArray ?
	_copy(x, xi, xj, xmodN, k - xn);
	return xmodN;
}

import {_alloc, _copy} from '@aureooms/js-integer-big-endian';

/**
 *
 * |x| >= k
 *
 */
export default function (k, x) {
	const xmodR = _alloc(k); // TODO Use UintXArray ?
	_copy(x, x.length - k, x.length, xmodR, 0);
	return xmodR;
}

[@aureooms/js-modular-arithmetic-big-endian](https://aureooms.github.io/js-modular-arithmetic-big-endian)
==

Modular arithmetic for JavaScript.
See [docs](https://aureooms.github.io/js-modular-arithmetic-big-endian/index.html).

> :warning: The code needs a ES2015+ polyfill to run (`regeneratorRuntime`),
> for instance [@babel/polyfill](https://babeljs.io/docs/usage/polyfill).

```js
import { Montgomery } from '@aureooms/js-modular-arithmetic-big-endian' ; 
const int = s => s.split('').map(x => +x) ;
const str = x => x.join('') ;
const base = 10 ;
const N = int('57896044618658097711785492504343953926634992332820282019728792003956564819949') ;
const mont = new Montgomery(base, N) ;

const a = int('57896044618658097711785492504343953926634992332820281301830804312103976049700') ;
const b = int('57896044618658097711785492504343953926634992332820282019728792003955491078125') ;
const _a = mont.from(a) ;
const _x = mont.pow(_a, b) ; // a^b % N
const x = mont.out(_x) ;
str(x) ; // 40504055762004792620159537441437949886475081163592261781667958256380085618313

const c = int('717897987691852588770249') ;
const _y = mont.pow(_a, c, false) ; // a^-c % N
const y = mont.out(_y) ;
str(y) ; // 51815386826945512755518685488363592823806772017922768894709047770322605499358
```

