import {
	_alloc as n_alloc,
	_zeros as n_zeros,
	_reset as n_reset,
	_copy as n_copy,
	jz as njz,
	_extended_euclidean_algorithm as n_extended_euclidean_algorithm,
	_trim_positive as n_trim_positive,
	_sub as n_sub,
	convert as nconvert,
} from '@aureooms/js-integer-big-endian';

import _mul from './_mul.js';
import _iadd from './_iadd.js';
import _isub from './_isub.js';
import _redc from './_redc.js';
import _montgomery from './_montgomery.js';
import modR from './modR.js';
import modN from './modN.js';

export default class Montgomery {
	constructor(b, N) {
		const {k, M, R, R2, R3} = _montgomery(b, N);
		this.b = b;
		this.N = N;
		this.k = k;
		this.M = M;
		this.R = R;
		this.R2 = R2;
		this.R3 = R3;
		// Use shared/pooled memory ?
	}

	one() {
		return this.R;
	}

	zero() {
		return n_zeros(this.k);
	}

	from(x) {
		// Conversion into Montgomery form is done by computing .
		// aR mod N = REDC((a mod N)(R^2 mod N))
		const _2kp1 = 2 * this.k + 1;
		const red = n_zeros(_2kp1); // TODO Use UintXArray ?
		const amodN = modN(this.b, this.N, x);
		_mul(this.b, this.N, this.M, this.R2, amodN, red);
		// TODO many unnecessary copies/alloc can be avoided by
		// allowing array offsets in methods.
		return modR(this.k, red);
	}

	out(aRmodN) {
		// Conversion out of Montgomery form is done by computing.
		// a mod N = REDC(aR mod N)
		const _2kp1 = 2 * this.k + 1;
		const _red = n_zeros(_2kp1); // TODO Use UintXArray ?
		n_copy(aRmodN, 0, this.k, _red, _2kp1 - this.k);
		_redc(this.b, this.k, this.N, 0, this.k, this.M, 0, this.k, _red, 0, _2kp1);
		const i = n_trim_positive(_red, this.k + 1, _2kp1);
		const red = n_alloc(_2kp1 - i); // TODO Use UintXArray ?
		n_copy(_red, i, _2kp1, red, 0);
		return red;
	}

	mul(aRmodN, bRmodN) {
		const _2kp1 = 2 * this.k + 1;
		const abRmodN = n_zeros(_2kp1);

		_mul(this.b, this.N, this.M, aRmodN, bRmodN, abRmodN);

		return modR(this.k, abRmodN);
	}

	add(aRmodN, bRmodN) {
		const aRpbRmodN = n_alloc(this.k);
		n_copy(aRmodN, 0, this.k, aRpbRmodN, 0);
		_iadd(this.b, this.N, aRpbRmodN, bRmodN);
		return aRpbRmodN;
	}

	sub(aRmodN, bRmodN) {
		const aRpbRmodN = n_alloc(this.k);
		n_copy(aRmodN, 0, this.k, aRpbRmodN, 0);
		_isub(this.b, this.N, aRpbRmodN, bRmodN);
		return aRpbRmodN;
	}

	inv(aRmodN) {
		// The modular inverse
		// Compute (aR mod N)^-1 using Euclidean algo
		const ai = n_trim_positive(aRmodN, 0, this.k);

		let [
			GCD,
			GCDi,
			// eslint-disable-next-line no-unused-vars
			_S,
			// eslint-disable-next-line no-unused-vars
			_Si,
			aRmodNi,
			// eslint-disable-next-line no-unused-vars
			_1,
			// eslint-disable-next-line no-unused-vars
			_2,
			// eslint-disable-next-line no-unused-vars
			_3,
			// eslint-disable-next-line no-unused-vars
			_4,
			// eslint-disable-next-line no-unused-vars
			_5,
			steps,
		] = n_extended_euclidean_algorithm(
			this.b,
			this.N,
			0,
			this.k,
			aRmodN,
			ai,
			this.k,
		);

		// Assert that GCD(N,aRmodN) is 1.
		if (GCD.length - GCDi !== 1 || GCD[GCDi] !== 1)
			throw new Error('aRmodN has no inverse modulo N');

		const _2kp1 = 2 * this.k + 1;
		const red = n_zeros(_2kp1); // TODO Use UintXArray ?

		if (steps % 2 === 1) {
			// We compute N - aRmodNi
			const temporary = n_zeros(this.k);
			n_sub(
				this.b,
				this.N,
				0,
				this.k,
				aRmodNi,
				0,
				this.k,
				temporary,
				0,
				this.k,
			);
			aRmodNi = temporary;
		}

		// A^-1 R mod N = REDC((aR mod N)^-1(R^3 mod N)).
		_mul(this.b, this.N, this.M, this.R3, aRmodNi, red);

		return modR(this.k, red);
	}

	pown(aRmodN, x) {
		// Modular
		// exponentiation can be done using exponentiation by squaring by initializing the
		// initial product to the Montgomery representation of 1, that is, to R mod N, and
		// by replacing the multiply and square steps by Montgomery multiplies.

		const nonneg = x >= 0;

		if (!nonneg) x = -x;

		if (x === 0) return this.R;
		if (x === 1) return nonneg ? aRmodN : this.inv(aRmodN);

		const xbits = [];

		do {
			xbits.push(x & 1);
			x >>= 1;
		} while (x !== 1);

		return this._powb(aRmodN, xbits, nonneg);
	}

	_powb(aRmodN, xbits, nonneg) {
		// The binary expansion of the exponent is 1 concatenanted with xbits
		// reversed. Must have xbits.length >= 1.
		const aRmodNpown = n_alloc(this.k);
		n_copy(aRmodN, 0, this.k, aRmodNpown, 0);

		const _2kp1 = 2 * this.k + 1;
		const temporary = n_alloc(_2kp1);

		do {
			n_reset(temporary, 0, _2kp1);
			_mul(this.b, this.N, this.M, aRmodNpown, aRmodNpown, temporary);
			n_copy(temporary, _2kp1 - this.k, _2kp1, aRmodNpown, 0);
			if (xbits.pop() === 1) {
				n_reset(temporary, 0, _2kp1);
				_mul(this.b, this.N, this.M, aRmodNpown, aRmodN, temporary);
				n_copy(temporary, _2kp1 - this.k, _2kp1, aRmodNpown, 0);
			}
		} while (xbits.length > 0);

		return nonneg ? aRmodNpown : this.inv(aRmodNpown);
	}

	pow(aRmodN, b, nonneg = true) {
		if (njz(b, 0, b.length - 1)) {
			// B consists of a single limb
			return this.pown(aRmodN, nonneg ? b[b.length - 1] : -b[b.length - 1]);
		}

		const xbits = nconvert(this.b, 2, b, 0, b.length);
		xbits.reverse();
		xbits.pop();

		return this._powb(aRmodN, xbits, nonneg);
	}
}

import {
	_iadd as n_iadd,
	_isub as n_isub,
	_cmp as n_cmp,
	_cmp_n as n_cmp_n,
} from '@aureooms/js-integer-big-endian';

import assert from 'assert';

/**
 *
 * @param {Number} r the radix
 * @param {Array} N number array of length k.
 * @param {Array} a
 * @param {Array} b
 *
 * (a+b)R mod N = aR mod N + bR mod N - ( 0/1 * N )
 *
 * R = r^k
 * N has no leading zeroes
 * N has length k
 * |N| = |a| >= |b|
 * |a| = |b| if you want to avoid side channel attacks
 * a < N
 * b < N
 *
 * t = a + b mod r^k
 * if t < b or t >= N then return t - N mod r^k
 * else return t // Can subtract dummy zero vector here in case we want
 *               // to avoid side channel attacks
 *
 */
export default function _iadd(r, N, a, b) {
	const k = N.length;

	assert(k >= 1);
	assert(N[0] !== 0);
	assert(a.length === k, '|a| !== k');
	assert(b.length <= k, '|b| > k');

	n_iadd(r, a, 0, k, b, 0, b.length);
	// TODO  Use overflow bit.
	// const overflow = _iadd(r, a, 0, k, b, 0, b.length) ;

	if (
		// Overflow
		n_cmp(a, 0, k, b, 0, b.length) < 0 ||
		n_cmp_n(a, 0, k, N, 0) >= 0
	) {
		n_isub(r, a, 0, k, N, 0, k); // Exploits wrapping
		return true;
	}

	return false;
}

import {
	_iadd as n_iadd,
	_isub as n_isub,
	_cmp as n_cmp,
} from '@aureooms/js-integer-big-endian';

import assert from 'assert';

/**
 *
 * @param {Number} r the radix
 * @param {Array} N number array of length k.
 * @param {Array} a
 * @param {Array} b
 *
 * (a+b)R mod N = aR mod N - bR mod N + ( 0/1 * N )
 *
 * R = r^k
 * N has no leading zeroes
 * N has length k
 * |N| = |a| >= |b|
 * |a| = |b| if you want to avoid side channel attacks
 * a < N
 * b < N
 *
 * t = a - b mod r^k
 * if a < b then return t + N mod r^k
 * else return t // Can add dummy zero vector here in case we want
 *               // to avoid side channel attacks
 *
 */
export default function _isub(r, N, a, b) {
	const k = N.length;

	assert(k >= 1);
	assert(N[0] !== 0);
	assert(a.length === k, '|a| !== k');
	assert(b.length <= k, '|b| > k');

	const underflow = n_cmp(a, 0, k, b, 0, b.length) < 0;
	n_isub(r, a, 0, k, b, 0, b.length);
	// TODO  Use underflow bit.
	// const underflow = n_isub(r, a, 0, k, b, 0, b.length) ;
	if (underflow) {
		n_iadd(r, a, 0, k, N, 0, k); // Exploits wrapping
		return true;
	}

	return false;
}

import {
	_alloc as n_alloc,
	_zeros as n_zeros,
	_copy as n_copy,
	_mul as n_mul,
	_idivmod as n_idivmod,
	_sub as n_sub,
	_extended_euclidean_algorithm as n_extended_euclidean_algorithm,
} from '@aureooms/js-integer-big-endian';

import _redc from './_redc.js';

import assert from 'assert';

/**
 *
 * N has no leading zeroes
 *
 * @param {Number} b the radix
 * @param {Array} N number array of length k.
 *
 *
 *
 *
 */
export default function _montgomery(b, N) {
	assert(N.length > 0);
	assert(N[0] !== 0);

	const k = N.length;

	const _2kp1 = 2 * k + 1;
	const _R = n_zeros(_2kp1);
	_R[k] = 1; // B^k

	const [
		GCD,
		GCDi,
		// eslint-disable-next-line no-unused-vars
		_S,
		// eslint-disable-next-line no-unused-vars
		_Si,
		_M,
		// eslint-disable-next-line no-unused-vars
		_1,
		// eslint-disable-next-line no-unused-vars
		_2,
		// eslint-disable-next-line no-unused-vars
		_3,
		// eslint-disable-next-line no-unused-vars
		_4,
		// eslint-disable-next-line no-unused-vars
		_5,
		steps,
	] = n_extended_euclidean_algorithm(b, _R, k, _2kp1, N, 0, k);

	// Assert that GCD(R,N) is 1.
	if (GCD.length - GCDi !== 1 || GCD[GCDi] !== 1)
		throw new Error('Montgomery: GCD(R,N) is not 1.');

	const M = n_alloc(k); // M mod R on k words
	if (steps % 2 === 0) {
		// We use _R[0:k] because it is filled with zeros.
		n_sub(b, _R, 0, k, _M, _M.length - k, _M.length, M, 0, k); // _M.length-k is always 1 ?
	} else {
		n_copy(_M, _M.length - k, _M.length, M, 0); // _M.length-k is always 1 ?
	}

	// R^2 mod N
	const _R2 = n_zeros(_2kp1);
	_R2[0] = 1;
	n_idivmod(b, _R2, 0, _2kp1, N, 0, k, _R, 0, _2kp1); // Use mod only function once implemented
	const R2 = n_alloc(k); // R^2 mod N on k words
	n_copy(_R2, k + 1, _2kp1, R2, 0);

	// Avoid using division for the computation of the other constants.
	// From Wikipedia:
	// Performing these operations requires knowing at least N′ and R2 mod N.
	// When R is a power of a small positive integer b, N′ can be computed by
	// Hensel's lemma: The inverse of N modulo b is computed by a naive
	// algorithm (for instance, if b = 2 then the inverse is 1), and Hensel's
	// lemma is used repeatedly to find the inverse modulo higher and higher
	// powers of b, stopping when the inverse modulo R is known; N′ is the
	// negation of this inverse. The constants R mod N and R3 mod N can be
	// generated as REDC(R2 mod N) and as REDC((R2 mod N)(R2 mod N)). The
	// fundamental operation is to compute REDC of a product. When standalone
	// REDC is needed, it can be computed as REDC of a product with 1 mod N. The
	// only place where a direct reduction modulo N is necessary is in the
	// precomputation of R2 mod N.

	// R mod N = REDC(R^2 mod N)
	_redc(b, k, N, 0, k, M, 0, k, _R2, 0, _2kp1);
	const R = n_alloc(k); // R mod N on k words
	n_copy(_R2, k + 1, _2kp1, R, 0);

	// R^3 mod N = REDC((R^2 mod N)(R^2 mod N))
	const _R3 = n_zeros(_2kp1);
	n_mul(b, R2, 0, k, R2, 0, k, _R3, 1, _2kp1);
	_redc(b, k, N, 0, k, M, 0, k, _R3, 0, _2kp1);
	const R3 = n_alloc(k); // R^3 mod N on k words
	n_copy(_R3, k + 1, _2kp1, R3, 0);

	// Console.debug({b, N, k, M, R, R2, R3});
	return {k, M, R, R2, R3};
}

import {_mul as n_mul} from '@aureooms/js-integer-big-endian';

import _redc from './_redc.js';

import assert from 'assert';

// TODO
// mul by non montgomery ?
// abR mod N = (aR mod N)(b mod N) MODULO????

/**
 *
 * abR mod N = REDC((aR mod N)(bR mod N))
 *
 *
 * |N| >= |a| >= |b|
 * |c| = 2*|N| + 1
 * c = 0000.0000 is zero initialized
 *
 */

export default function _mul(r, N, M, a, b, c) {
	const k = N.length;
	const _2kp1 = 2 * k + 1;

	assert(a.length <= k, '|a| > |N|');
	assert(b.length <= a.length, '|b| > |a|');
	assert(c.length === _2kp1, '|c| !== 2*k+1');

	// C = (aR mod N)(bR mod N)
	n_mul(
		r,
		a,
		0,
		a.length,
		b,
		0,
		b.length,
		c,
		_2kp1 - a.length - b.length,
		_2kp1,
	);

	// C = REDC((aR mod N)(bR mod N))
	return _redc(r, k, N, 0, k, M, 0, k, c, 0, _2kp1);
}

import {
	_zeros as n_zeros,
	_copy as n_copy,
	_reset as n_reset,
	_cmp_n as n_cmp_n,
	_mul as n_mul,
	_iadd as n_iadd,
	_isub as n_isub,
} from '@aureooms/js-integer-big-endian';

import assert from 'assert';

/**
 * Function REDC is
 *
 *  input: Integers R = b^k > N with gcd(R, N) = 1,
 *         Integer M in [0, R − 1] such that NM ≡ −1 mod R,
 *         Integer T in the range [0, RN − 1]
 *
 *         All numbers are given in base b (big endian order).
 *  output: Integer S in the range [0, N − 1] such that S ≡ TR−1 mod N
 *
 *  m ← ((T mod R)M) mod R // Can be implemented by discarding limbs
 *  t ← (T + mN) / R // Can be implemented with a shift
 *  // /!\ T + mN is potentially RN - 1 + (R-1) N = 2RN - N - 1 so need one
 *  // extra limb for carry ?
 *  if t ≥ N then
 *   return t − N
 *  else
 *   return t // Can add dummy - zero vector here in case we want to avoid side
 *            // channel attacks
 *  end if
 * end function
 *
 */
export default function _redc(b, k, N, Ni, Nj, M, Mi, Mj, T, Ti, Tj) {
	assert(Nj - Ni === k, '|N| !== k');
	assert(Mj - Mi === k, '|M| !== k'); // Can allow |M| <= k here.
	assert(Tj - Ti === 2 * k + 1, '|T| !== 2*k+1');
	assert(T[Ti] === 0, 'T[Ti] !== 0');

	// Reduce T mod R
	const _Ti = Tj - k;
	const _2k = k << 1;

	const m = n_zeros(_2k);
	const mj = _2k;

	// M = ((T mod R) M) mod R
	n_mul(b, T, _Ti, Tj, M, Mi, Mj, m, 0, mj);
	// Could be even more efficient here
	// if we had a multiplication method that discards higher order
	// bits

	const mi = k; // M = m mod R

	// X = m * N
	const X = n_zeros(_2k); // TODO mutualize allocation with m
	const Xj = _2k;
	n_mul(b, m, mi, mj, N, Ni, Nj, X, 0, Xj);

	// T = T + X = T + mN
	n_iadd(b, T, Ti, Tj, X, 0, Xj);

	// T = T / R
	assert(T[Ti] === 0, 'T[Ti] !== 0');
	n_copy(T, Ti + 1, _Ti, T, _Ti);
	// Assert T[Ti] === 0
	const _Ti_1 = _Ti;
	// T[_Ti_1] = T[Ti];
	n_reset(T, Ti, _Ti_1);

	// If t ≥ N then
	if (n_cmp_n(T, _Ti_1, Tj, N, Ni) >= 0) {
		n_isub(b, T, _Ti_1, Tj, N, Ni, Nj); // Return t − N
		return true;
	}

	return false;
	// Else return t
}

export {default as Montgomery} from './Montgomery.js';
export {default as _iadd} from './_iadd.js';
export {default as _isub} from './_isub.js';
export {default as _montgomery} from './_montgomery.js';
export {default as _mul} from './_mul.js';
export {default as _redc} from './_redc.js';
export {default as modN} from './modN.js';
export {default as modR} from './modR.js';

import {
	_alloc as n_alloc,
	_zeros as n_zeros,
	_copy as n_copy,
	_idivmod as n_idivmod,
	_trim_positive as n_trim_positive,
	_cmp_n as n_cmp_n,
} from '@aureooms/js-integer-big-endian';

import modR from './modR.js';

export default function modN(r, N, x) {
	const k = N.length;
	const xj = x.length;
	const xi = n_trim_positive(x, 0, xj);
	const xn = xj - xi;
	if (xn > k || (xn === k && n_cmp_n(x, xi, xj, N, 0) >= 0)) {
		const xmodN = n_alloc(xn); // TODO Use UintXArray ?
		n_copy(x, xi, xj, xmodN, 0);
		const _ = n_zeros(xn); // TODO use _imod once implemented
		n_idivmod(r, xmodN, 0, xn, N, 0, k, _, 0, xn);
		return modR(k, xmodN);
	}

	const xmodN = n_zeros(k); // TODO Use UintXArray ?
	n_copy(x, xi, xj, xmodN, k - xn);
	return xmodN;
}

import {
	_alloc as n_alloc,
	_copy as n_copy,
} from '@aureooms/js-integer-big-endian';

import assert from 'assert';

/**
 *
 * |x| >= k
 *
 */
export default function modR(k, x) {
	assert(x.length >= k, '|x| >= k');

	const xmodR = n_alloc(k); // TODO Use UintXArray ?
	n_copy(x, x.length - k, x.length, xmodR, 0);
	return xmodR;
}

